import React from "react";
import { useContext } from "react";
import { AppContext } from "../context/AppContext";
import Spinner from "./Spinner";

const Blogs = () => {

  const {posts, loading} = useContext(AppContext);

  return (
    <div>
      {
        loading ?
        (<Spinner />) :

        (
          posts.length === 0 ? 
          (
            <div>
              <p>No post Found</p>
            </div>
          ) :
          (posts.map( (post) => (<card />)))
        )
      }
    </div>
  )
}

export default Blogs ;
